INSERT INTO `tbComments` VALUES (2711, NULL, '孩子一直都是看的党西强教授，每个月19日固定的减少一粒泥泼松，另外每个月的复查，尿检都是正常值', '肾病综合征', '很好（10分）', '很好（10分）', '很好（10分）', 1, '0.0114468992609286');
