INSERT INTO `tbComments` VALUES (2715, NULL, '我患的是子宫肌瘤，新华医院的妇科张萍主任为我做的微创手术，手术很成功术后无任何异常，现恢复良好，感谢张主任！祝您永远年轻漂亮、健康幸福！', '妇科', '很差（0分）', '很差（0分）', '很差（0分）', 0, '0.999999994908658');
