INSERT INTO `tbComments` VALUES (2802, NULL, '长征医院普外科郑向民滚出医生队伍，只知道收红包，这种人渣应该下十八层地狱去赎罪，免得再在人间祸害更多患者，造成患者身心和经济双重负担。', '内科', '很差（0分）', '很差（0分）', '很差（0分）', 0, '0.997376828003435');
