INSERT INTO `tbComments` VALUES (3358, NULL, '多年一直范疼的颈椎病在这次到天津海河医院的治疗中得到了很好的改善，这次的治疗效果真的让我非常满意，病人是最清楚治疗效果的。', '颈椎病', '很好（10分）', '很好（10分）', '很好（10分）', 1, '0.999874896562445');
