INSERT INTO `tbComments` VALUES (3370, NULL, '胆结石在一般的医院不好治，人家会让你转院，在这家医院治疗好我的胆结石的，说明人家有经验也有信心治好这个病。', '胆结石', '很好（10分）', '很好（10分）', '很好（10分）', 1, '0.113293207476859');
