INSERT INTO `tbComments` VALUES (4018, NULL, '第三住院部六楼10号床和正对面的病房的陪人，总是在病房乱拉线煮饭，搞得全是油烟味，那些护士也包庇他们，让病人不好养病。', '内科', '很差（0分）', '很差（0分）', '很差（0分）', 0, '0.00501505587725659');
