INSERT INTO `tbComments` VALUES (4079, NULL, '得了妇科炎症，自己去买药吃没什么管用，就去医院看了医生，医生给开了药，吃了几天就好了，非常管用。', '妇科病', '很好（10分）', '很好（10分）', '很好（10分）', 1, '0.821486155000205');
