INSERT INTO `tbComments` VALUES (4456, NULL, '医生工作非常认真，经验丰富，在看了我的化验报告后，仔细帮我分析病因，非常耐心，解除了我的疑惑，非常感谢!', '干燥综合征', '很好（10分）', '很好（10分）', '很好（10分）', 1, '0.999383725741092');
