INSERT INTO `tbComments` VALUES (4917, NULL, '妇产科主任谢祝英完全是变色龙，一听说是在哪上班有单位的马上就轻声细语一看穿着不咋样的马上就狂风暴雨。不知道中心医院其他医生是不是也这样，不知道医德何在。', '内科', '很差（0分）', '很差（0分）', '很差（0分）', 0, '0.0545544338011267');
