INSERT INTO `tbComments` VALUES (5880, NULL, '非常好的医院，很大，医生特别好，很耐心，讲解的很详细根本不像有些医院依赖就做一堆检查，排队很久1分钟就打发了，很负责任，非常感谢', '多动症', '很好（10分）', '很好（10分）', '很好（10分）', 1, '0.99038042474345');
