INSERT INTO `tbComments` VALUES (5990, NULL, '军区总医院耳鼻喉科住院部张龙芳，态度及其恶劣，患者不想做不明不白的手术，大声呵斥患者，周围的医生都帮着说话。', '内科', '很差（0分）', '很差（0分）', '很差（0分）', 0, '0.000110924127884382');
