INSERT INTO `tbComments` VALUES (5998, NULL, '每天医院那边看病的都很多人，确实不错，因为发现的早，去治疗了两次就没有耳朵难受了，不错！', '中耳炎', '很好（10分）', '很好（10分）', '好（8分）', 1, '0.985895539144369');
