INSERT INTO `tbComments` VALUES (6016, NULL, '医院的医生很热心，耐心，很仔细的看每一张化验单和检查报告，医生的态度让我非常感动，非常敬业，真的是一位名副其实的好医生。', '妇科疾病', '很好（10分）', '好（8分）', '很好（10分）', 1, '0.999903442669727');
