INSERT INTO `tbComments` VALUES (6058, NULL, '这是我第一次感觉，医院虽然不是特别大，但是护士、医生的服务态度还是很不错的，不像有些医院花钱才给你治疗，赞一个！', '骨科疾病', '很好（10分）', '好（8分）', '很好（10分）', 1, '0.983165384390747');
