INSERT INTO `tbComments` VALUES (6064, NULL, '医院给我整体感觉很不错，干净，明亮，卫生特别好，医生也很尽责，治疗效果也很满意。', '妇科病', '很好（10分）', '好（8分）', '很好（10分）', 1, '0.999946284486191');
