INSERT INTO `tbComments` VALUES (6066, NULL, '服务满意，医院环境、医生态度、等待时间、就诊时间、手术方式及手术效果很满意，宝宝现在很可爱，等我坐完月子一定送锦旗回医院。', '生产', '很好（10分）', '很好（10分）', '好（8分）', 1, '0.999930103749537');
