INSERT INTO `tbComments` VALUES (6078, NULL, '硬件不错，软件方面、医疗水平没得说，各种进口仪器和先进技术，医护人员服务态度还多好的。', '骨折', '好（8分）', '很好（10分）', '很好（10分）', 1, '0.989175917464468');
INSERT INTO `tbComments` VALUES (6079, NULL, '家人开刀，外科。医生很年轻，严谨，专业，跟家属沟通的时候各方面事宜都介绍详尽，因为这名医生对医院好感大增。', '腰腿痛', '好（8分）', '好（8分）', '一般（6分）', 1, '0.998541035257453');
