INSERT INTO `tbComments` VALUES (6416, NULL, '一到冬天鼻炎就越发严重，朋友介绍去贵州省第二人民医院治疗，结果鼻炎就给治好了，真心感谢医院！', '鼻炎', '很好（10分）', '很好（10分）', '很好（10分）', 1, '0.999839383685015');
