INSERT INTO `tbComments` VALUES (6693, NULL, '想问一下 我妹妹14岁 钻掉耳朵里了  在镇上都不敢给弄 绥化医院能弄出来嘛', '内科', '很差（0分）', '很差（0分）', '很差（0分）', 0, '0.00164103533769067');
